import React, { Component } from "react";
import { Drawer, Form, Col, Row, Input, Select, DatePicker } from 'antd';
import Button from "antd/es/button";
import Avatar from "antd/es/avatar";
import UserOutlined from "@ant-design/icons/lib/icons/UserOutlined";
import MailOutlined from "@ant-design/icons/lib/icons/MailOutlined";
import WhatsAppOutlined from "@ant-design/icons/lib/icons/WhatsAppOutlined";
import Divider from "antd/es/divider";
import EditOutlined from "@ant-design/icons/lib/icons/EditOutlined";
import ContainerOutlined from "@ant-design/icons/lib/icons/ContainerOutlined";
import Upload from "antd/es/upload";
import UploadOutlined from "@ant-design/icons/lib/icons/UploadOutlined";
import StudentService from "./StudentService";
import AuthenticationService from "./AuthenticationService";
import jwt_decode from "jwt-decode";
const { Option } = Select;

class StudentProfileManagerComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            visible: false,
            fistName: "",
            lastName: "",
            phone: "",
            email: "",
            institution:"",
        };
        console.log(this.state.email)
        this.showDrawer = this.showDrawer.bind(this);
        this.onClose = this.onClose.bind(this);
    }

    showDrawer = () => {

        this.setState({
            visible: true,
        });
        console.log(this.state.visible)
    };

    onClose = () => {
        this.setState({
            visible: false,
        });
    };

    componentDidMount() {
        let fistName;
        let lastName;
        let phone;
        let email;
        let institution;
        StudentService.getUserProfile().then(response => {
            console.log(response.data)
            fistName = response.data.userinfo.firstName;
            lastName = response.data.userinfo.LastName;
            phone = response.data.phone;
            email = response.data.email;
            institution = response.data.userinfo.institution;
            this.setState({
                fistName: fistName,
                lastName: lastName,
                phone: phone,
                email: email,
                institution: institution,
            });
        })

