import React, {Component} from "react";
import {Rate} from "antd";
import Button from "antd/es/button";
import {floor} from "lodash";

class StudentRateItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: 0,
        };
    }

    handleChange = value => {
        this.setState({ value });
        console.log(value)
    };

    render() {
        const project = this.props.project;
        const { value } = this.state;
        const desc = ['terrible', 'bad', 'normal', 'good', 'wonderful'];
        return (
            <div className="ant-card-bordered student-detail-container">
                <div className="project-detail">
                    <div className="project-detail-head">
                        <div className="project-detail-head-wrapper" >
                            <div className="project-detail-head-title">
                                <img src="https://assets.laioffer.com/public/companyLogo/9b7556fe-3187-4f58-b290-b9a1348db9a3.png" height="120px" width="120px" className="project-img"/>
                                <div className="project-title-right">
                                    <h2>{project.companyName} </h2>
                                    <span>{project.project.projectName}</span><br/>
                                    <span>
                                        <Rate tooltips={desc} onChange={this.handleChange} value={value} allowHalf />
                                        {value ? <span className="ant-rate-text">{desc[floor(value - 1)]}</span> : ''}
                                    </span><br/>
                                    <span>status: {project.status}</span>
                                </div>
                            </div>
                            <div className="project-apply">
                                <Button type="primary" shape="round" size="large">
                                    Rate
                                </Button>
                            </div>
                            <div className="project-clear"></div>
                        </div>
                    </div>

                </div>
            </div>

        );
    }
}

export default StudentRateItem;
