import React, {Component} from "react";
import CompanyService from "./CompanyService";
import {Button, Col, Form, Input, message, Row} from "antd";
import {InboxOutlined, PlusCircleOutlined} from "@ant-design/icons";
import Dragger from "antd/es/upload/Dragger";
import Divider from "antd/es/divider";
import ContainerOutlined from "@ant-design/icons/lib/icons/ContainerOutlined";
import List from "antd/es/list";
import Skeleton from "antd/es/skeleton";
import Avatar from "antd/es/avatar";

class CompanyProjectComponent extends Component{
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.match.params.id,
            description: '',
            fileList: '',
            projectUrl: '',
            projectName:'',
            status:'',
            applicants: [],
            refresh: "",
        }

    }

    refresh= () => {
        CompanyService.getProjectsById(this.state.id).then(response => {
            this.setState({
                description: response.data.description,
                projectUrl:response.data.projectUrl,
                projectName: response.data.projectName,
                status: response.data.status,
            })
        })

        CompanyService.getAppliedStudentsOfProject(this.state.id).then(response => {
            this.setState({
                applicants: response.data
            })
        });
    }
    componentDidMount() {
        this.refresh();
    }

    handelNameChange = (e) => {
        this.setState({
            projectName: e.target.value
        })
        console.log(this.state)
    }

    handelDescriptionChange = (e) => {
        this.setState({
            description: e.target.value
        })
    }

    acceptClick = (e) => {
        console.log(e)
    }

    render() {

        console.log(this.state)

        const {fileList, description,projectName, status, id} = this.state;
        const props = {

            onRemove: file => {
                this.setState(state => {
                    const index = state.fileList.indexOf(file);
                    const newFileList = state.fileList.slice();
                    newFileList.splice(index, 1);
                    return {
                        fileList: newFileList,
                    };
                });
            },
            beforeUpload: file => {
                this.setState(state => ({
                    fileList: [...state.fileList, file],
                }));
                return false;
            },
            fileList,
            accept: ".doc, .docx, .pdf"
        };
        console.log(projectName)
        return (
            <div className="ant-card-bordered project-detail-container">
                <div>
                        <Form
                            layout="vertical"
                        >
                            <Row gutter={16}>
                                <Col span={12}>

                                    <Form.Item
                                        name="Project Name"
                                        label="Project Name"
                                        rules={[
                                            { required: true, message: 'Please enter project name' }
                                        ]}
                                    >

                                        <Input placeholder="Please enter project name" defaultValue={projectName} />
                                    </Form.Item>
                                </Col>
                                <Col span={12} push={5}>
                                    status: {status} &nbsp;
                                    <br/>
                                    <br/>
                                    <Button type="primary"  size="small">
                                        finish project
                                    </Button>
                                </Col>

                            </Row>
                            <Row gutter={16}>
                                <Col span={24}>
                                    <Form.Item
                                        name="description"
                                        label="Description"
                                        rules={[
                                            {
                                                required: true,
                                                message: 'description of the project',
                                            },
                                        ]}
                                    >
                                        <Input.TextArea rows={4} placeholder="please enter the description" value={description}
                                                        onChange={this.handelDescriptionChange.bind(this)}/>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={[0, 30]}>
                                <a href={this.state.projectUrl} className="resume-font"><ContainerOutlined /> &nbsp; detail.pdf</a>
                            </Row>
                            <Row gutter={30}>
                                <Col span={24}>
                                    <Dragger {...props} >
                                        <p className="ant-upload-drag-icon">
                                            <InboxOutlined />
                                        </p>
                                        <p className="ant-upload-text">Click or drag file to this area to upload</p>
                                        <p className="ant-upload-hint">
                                            Support for a single or bulk upload. Strictly prohibit from uploading company data or other
                                            band files
                                        </p>
                                    </Dragger>
                                </Col>

                            </Row>
                            <Row gutter={30}>
                                <Col push={20}>
                                    <Form.Item
                                    >
                                        <br/>
                                    <Button type="primary" size="large">
                                        update
                                    </Button>
                                </Form.Item>
                                </Col>
                            </Row>

                        </Form>
                </div>
                <Divider />

                <List
                    className="demo-loadmore-list"
                    itemLayout="horizontal"
                    dataSource={this.state.applicants}
                    renderItem={item => (

                            <List.Item

                                actions={[<a>status: {item.status}</a>,
                                    <a onClick={e => {
                                        CompanyService.approveApplication(this.state.id, item.student_id).then(response => {
                                            message.success("success")
                                            this.refresh();
                                        }).catch(error => {
                                            message.error("failed")
                                        })
                                    }} >accept</a>, <a
                                    onClick={e =>{

                                    }}>decline</a>]}
                            >

                            <Skeleton avatar title={false} loading={item.loading} active>
                                <List.Item.Meta
                                    avatar={
                                        <Avatar src={item.student.iconUrl} />
                                    }
                                    title={<a href="https://ant.design">{item.student.firstName} {item.student.lastName}</a>}
                                    description={item.student.institution}
                                />

                            </Skeleton>
                        </List.Item>
                    )}
                    />
            </div>
        );
    }
}

export default CompanyProjectComponent;
