import React, { Component } from 'react';
import Route from "react-router-dom/es/Route";
import TopNavBar from "./TopNavBar";
import AuthenticationService from "./AuthenticationService";
import jwt_decode from "jwt-decode";
import {withRouter} from 'react-router-dom';
import CompanyProjectManagerComponent from "./CompanyProjectManagerComponent";
import CompanyRecommendComponent from "./CompanyRecommendComponent";
import CompanyProfileManagerComponent from "./CompanyProfileManagerComponent";

class CompanyPage extends Component{
    constructor(props) {
        super(props);
        const user = AuthenticationService.getCurrentUser();
        this.state = {
           role: '',
        };
        if (user) {
            const decode = jwt_decode(user.token)
            const role = decode.role
            this.setState({
                role: role,
            });
            if (role === "student") {
                props.history.push("/student")
            }
            console.log(decode.role)
        } else {
            props.history.push("/login")
        }
    }
    render() {
        return (
            <div className="App">
                <TopNavBar role={this.state.role}/>
                This is a company page
                <div className="App">
                    <Route path="/company/profile/"  component={CompanyProfileManagerComponent} />
                    <Route path="/company/projects/" component={CompanyProjectManagerComponent} />
                    <Route path="/company/recommendation/" component={CompanyRecommendComponent} />
                </div>
            </div>
        )
    }
}

export default withRouter(CompanyPage);
