import React, {Component} from "react";
import CompanyService from "../service/CompanyService";
import {Button, Col, Form, Input, Modal, Row} from "antd";
import {InboxOutlined, PlusCircleOutlined} from "@ant-design/icons";
import Dragger from "antd/es/upload/Dragger";
import Descriptions from "antd/es/descriptions";
import Badge from "antd/es/badge";

class CompanyStudentModal extends Component {
    constructor(props) {
        super(props);
        console.log(props.student.student.firstName)
        this.state = {
            ModalText: 'Content of the modal',
            visible: false,
            confirmLoading: false,
            fileList: [],
            firstName: props.student.student.firstName,
            lastName: props.student.student.lastName,
            institution: props.student.student.institution,
            studentId: props.student.student_id,
            status: props.student.status,
        };
        this.props = props;
    }


    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = (values) => {
        const {projectName, description, fileList} = this.state;
        const formData = new FormData();
        fileList.forEach(file => {
            formData.append('file', file);
        });
        formData.append('projectName', projectName);
        formData.append('description', description);
        CompanyService.createProject(formData).then(response => {
            console.log(response.data)
            this.props.refresh();
        })
        this.setState({
            confirmLoading: true,
        });
        setTimeout(() => {
            this.setState({
                visible: false,
                confirmLoading: false,
            });

        }, 500);


    };

    handleCancel = () => {
        console.log('Clicked cancel button');
        this.setState({
            visible: false,
        });
    };

    handelNameChange = (e) => {
        this.setState({
            projectName: e.target.value
        })
    }

    handelDescriptionChange = (e) => {
        this.setState({
            description: e.target.value
        })
    }

    render() {
        const { visible, confirmLoading } = this.state;
        const { fileList, projectName, description } = this.state;
        const props = {
            onRemove: file => {
                this.setState(state => {
                    const index = state.fileList.indexOf(file);
                    const newFileList = state.fileList.slice();
                    newFileList.splice(index, 1);
                    return {
                        fileList: newFileList,
                    };
                });
            },
            beforeUpload: file => {
                this.setState(state => ({
                    fileList: [...state.fileList, file],
                }));
                return false;
            },
            fileList,
            accept: ".doc, .docx, .pdf"
        };
        return (
            <div>
                <a  onClick={this.showModal}>
                    {this.state.firstName} {this.state.lastName}
                </a>
                <Modal
                    title="Add a new object"
                    visible={visible}
                    onOk={this.handleOk}
                    confirmLoading={confirmLoading}
                    onCancel={this.handleCancel}
                    okText="submit"
                >
                    <Descriptions title="User Info" bordered>
                    <Descriptions.Item label="Product">Cloud Database</Descriptions.Item>
                    <Descriptions.Item label="Billing Mode">Prepaid</Descriptions.Item>
                    <Descriptions.Item label="Automatic Renewal">YES</Descriptions.Item>
                    <Descriptions.Item label="Order time">2018-04-24 18:00:00</Descriptions.Item>
                    <Descriptions.Item label="Usage Time" span={2}>
                        2019-04-24 18:00:00
                    </Descriptions.Item>
                    <Descriptions.Item label="Status" span={3}>
                        <Badge status="processing" text="Running" />
                    </Descriptions.Item>
                    <Descriptions.Item label="Negotiated Amount">$80.00</Descriptions.Item>
                    <Descriptions.Item label="Discount">$20.00</Descriptions.Item>
                    <Descriptions.Item label="Official Receipts">$60.00</Descriptions.Item>
                    <Descriptions.Item label="Config Info">
                        Data disk type: MongoDB
                        <br />
                        Database version: 3.4
                        <br />
                        Package: dds.mongo.mid
                        <br />
                        Storage space: 10 GB
                        <br />
                        Replication factor: 3
                        <br />
                        Region: East China 1<br />
                    </Descriptions.Item>
                </Descriptions>,

                    <Dragger {...props} >
                        <p className="ant-upload-drag-icon">
                            <InboxOutlined />
                        </p>
                        <p className="ant-upload-text">Click or drag file to this area to upload</p>
                        <p className="ant-upload-hint">
                            Support for a single or bulk upload. Strictly prohibit from uploading company data or other
                            band files
                        </p>
                    </Dragger>

                </Modal>
            </div>
        );
    }
}

export default CompanyStudentModal;
