import {Modal, Button, Row, Col, Form, Input} from 'antd';
import React, {Component} from "react";
import { InboxOutlined, PlusCircleOutlined } from '@ant-design/icons';
import { Upload, message } from 'antd';

const { Dragger } = Upload;

class CompanyAddProjectModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            ModalText: 'Content of the modal',
            visible: false,
            confirmLoading: false,
        };
    }


    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = (values) => {
        console.log(values)
        this.setState({
            ModalText: 'The modal will be closed after two seconds',
            confirmLoading: true,
        });
        setTimeout(() => {
            this.setState({
                visible: false,
                confirmLoading: false,
            });
        }, 2000);
    };

    handleCancel = () => {
        console.log('Clicked cancel button');
        this.setState({
            visible: false,
        });
    };

    render() {
        const { visible, confirmLoading, ModalText } = this.state;
        const props = {
            name: 'file',
            multiple: true,
            action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
            onChange(info) {
                const { status } = info.file;
                if (status !== 'uploading') {
                    console.log(info.file, info.fileList);
                }
                if (status === 'done') {
                    message.success(`${info.file.name} file uploaded successfully.`);
                } else if (status === 'error') {
                    message.error(`${info.file.name} file upload failed.`);
                }
            },
        };

        return (
            <div>
                <Button type="primary" onClick={this.showModal}>
                    <PlusCircleOutlined />Add project
                </Button>
                <Modal
                    title="Add a new object"
                    visible={visible}
                    onOk={this.handleOk}
                    confirmLoading={confirmLoading}
                    onCancel={this.handleCancel}
                    okText="submit"
                >
                    <Form
                        layout="vertical"
                        hideRequiredMark
                        onFinish={this.update}
                        initialValues={{
                            residence: ['zhejiang', 'hangzhou', 'xihu'],
                            prefix: '1',
                        }}
                    >
                        <Row gutter={16}>
                            <Col span={12}>
                                <Form.Item
                                    name="Project Name"
                                    label="Project Name"
                                    rules={[
                                        { required: false, message: 'Please enter first name' }
                                    ]}
                                >
                                    <Input placeholder="Please enter user name" defaultValue={this.state.fistName}/>
                                </Form.Item>
                            </Col>

                        </Row>
                        <Row gutter={16}>
                            <Col span={24}>
                                <Form.Item
                                    name="description"
                                    label="Description"
                                    rules={[
                                        {
                                            required: false,
                                            message: 'please introduce yourself',
                                        },
                                    ]}
                                >
                                    <Input.TextArea rows={4} placeholder="please enter the description" />
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Col span={24}>
                                <Dragger {...props}>
                                    <p className="ant-upload-drag-icon">
                                        <InboxOutlined />
                                    </p>
                                    <p className="ant-upload-text">Click or drag file to this area to upload</p>
                                    <p className="ant-upload-hint">
                                        Support for a single or bulk upload. Strictly prohibit from uploading company data or other
                                        band files
                                    </p>
                                </Dragger>
                            </Col>

                        </Row>

                    </Form>
                </Modal>
            </div>
        );
    }
}

export default CompanyAddProjectModal;
