import {Modal, Button, Row, Col, Form, Input} from 'antd';
import React, {Component} from "react";
import PlusCircleOutlined from "@ant-design/icons/lib/icons/PlusCircleOutlined";

class CompanyAddProjectModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            ModalText: 'Content of the modal',
            visible: false,
            confirmLoading: false,
        };
    }


    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = () => {
        this.setState({
            ModalText: 'The modal will be closed after two seconds',
            confirmLoading: true,
        });
        setTimeout(() => {
            this.setState({
                visible: false,
                confirmLoading: false,
            });
        }, 2000);
    };

    handleCancel = () => {
        console.log('Clicked cancel button');
        this.setState({
            visible: false,
        });
    };

    render() {
        const { visible, confirmLoading, ModalText } = this.state;
        return (
            <div>
                <Button type="primary" onClick={this.showModal}>
                    <PlusCircleOutlined />Add project
                </Button>
                <Modal
                    title="Add a new object"
                    visible={visible}
                    onOk={this.handleOk}
                    confirmLoading={confirmLoading}
                    onCancel={this.handleCancel}
                    okText="submit"
                >
                    <Form
                        layout="vertical"
                        hideRequiredMark
                        onFinish={this.update}
                        initialValues={{
                            residence: ['zhejiang', 'hangzhou', 'xihu'],
                            prefix: '1',
                        }}
                    >
                        <Row gutter={16}>
                            <Col span={12}>
                                <Form.Item
                                    name="first_name"
                                    label="First Name"
                                    rules={[
                                        { required: false, message: 'Please enter first name' }
                                    ]}
                                >
                                    <Input placeholder="Please enter user name" defaultValue={this.state.fistName}/>
                                </Form.Item>
                            </Col>
                            <Col span={12}>
                                <Form.Item
                                    name="last_name"
                                    label="Last Name"
                                    rules={[{ required: false, message: 'Please enter Last name' }]}
                                >
                                    <Input placeholder="Please enter user name" defaultValue={this.state.lastName}/>
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Col span={12}>
                                <Form.Item
                                    name="phone"
                                    label="Phone Number"
                                    rules={[{ required: false, message: 'Please input your phone number!' }]}
                                >
                                    <Input addonBefore={prefixSelector} style={{ width: '100%' }} defaultValue={this.state.phone}/>
                                </Form.Item>
                            </Col>
                            <Col span={12}>
                                <Form.Item
                                    name="university"
                                    label="University"
                                    rules={[{ required: false, message: 'Please enter your university' }]}
                                >
                                    <Input placeholder="Please enter user university" defaultValue={this.state.institution}/>
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Col span={24}>
                                <Form.Item
                                    name="description"
                                    label="Description"
                                    rules={[
                                        {
                                            required: false,
                                            message: 'please introduce yourself',
                                        },
                                    ]}
                                >
                                    <Input.TextArea rows={4} placeholder="please enter the description" />
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row gutter={16}>
                            <Form.Item >
                                <Button type="primary" htmlType="submit">
                                    Submit
                                </Button>
                            </Form.Item>
                        </Row>
                    </Form>
                </Modal>
            </div>
        );
    }
}

export default CompanyAddProjectModal;
